import React, { useState } from "react";
import NoteContext from "./noteContext";

const NoteState = (props) => {
  const host = "http://localhost:8000";

  const notesInitial = [];

  const [notes, setNotes] = useState(notesInitial);

  //==========================================Get all Notes ==============================================================
  const getNotes = async () => {
    //API call
    const response = await fetch(`${host}/notes/fetchallnotes`, {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
        "auth-token":
          "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjZmMDcyMzA2NWVmNTViYjdlOGExYzg4In0sImlhdCI6MTcyNzAzMzkzMX0.m65flM3XKhHpIqa1lRJ498LO8zcsveNtbsbqjy99wEk",
      },
    });
    const json = await response.json();
    setNotes(json);
  };

  //==========================================Add a note ==============================================================
  const addNote = async (title, description, tag) => {
    //API call
    const response = await fetch(`${host}/notes/addnotes`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        "auth-token":
          "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjZmMDcyMzA2NWVmNTViYjdlOGExYzg4In0sImlhdCI6MTcyNzAzMzkzMX0.m65flM3XKhHpIqa1lRJ498LO8zcsveNtbsbqjy99wEk",
      },
      body: JSON.stringify({ title, description, tag }),
    });
    const json = await response.json();
    console.log(json);

    const newNote = {
      _id: {
        $oid: "166f072d465ef55bssb7e8a1c8f",
      },
      user: {
        $oid: "66f07asa23065ef55bb7e8a1c88",
      },
      title: title,
      description: description,
      tag: tag,
      __v: 0,
    };

    setNotes(notes.concat(newNote));
  };

  //==========================================Delete a note ==============================================================
  const deleteNote = async (id) => {
    //API call
    const response = await fetch(`${host}/notes/deletenotes/${id}`, {
      method: "DELETE",
      headers: {
        "Content-Type": "application/json",
        "auth-token":
          "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjZmMDcyMzA2NWVmNTViYjdlOGExYzg4In0sImlhdCI6MTcyNzAzMzkzMX0.m65flM3XKhHpIqa1lRJ498LO8zcsveNtbsbqjy99wEk",
      },
    });
    const json = await response.json();
    setNotes(json);

    const newNote = notes.filter((note) => {
      return note._id !== id;
    });
    setNotes(newNote);
  };

  //==========================================Edit a note ==============================================================
  const editNote = async (id, title, description, tag) => {
    //API call
    const response = await fetch(`${host}/notes/updatenotes/${id}`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
        "auth-token":
          "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjZmMDcyMzA2NWVmNTViYjdlOGExYzg4In0sImlhdCI6MTcyNzAzMzkzMX0.m65flM3XKhHpIqa1lRJ498LO8zcsveNtbsbqjy99wEk",
      },
      body: JSON.stringify({ title, description, tag }),
    });
    const json = await response.json();
    console.log(json);

    let newNotes = JSON.parse(JSON.stringify(notes))
    for (let index = 0; index < newNotes.length; index++) {
      const element = newNotes[index];
      if (element._id === id) {
        element.title = title;
        newNotes[index].description = description;
        newNotes[index].tag = tag;
        break;
      }
    }
    setNotes(newNotes)
  };

  return (
    <NoteContext.Provider
      value={{ notes, addNote, deleteNote, editNote, getNotes }}
    >
      {props.children}
    </NoteContext.Provider>
  );
};
export default NoteState;
